import React, { useState } from 'react'
import '../css/Menu.css'

function Menu() {

    const [Score, SetScore] = useState(0);

    const OpenForm = () => {
        SetScore(1)
    };

    return (
        <div className="menu_system">
            <button onClick={OpenForm} className="add__btn">Добавить</button>
            {
                Score === 0
                    ? style={body: {display: none}} 
                    : false
            }
        </div>
    )
}

export default Menu
