import React from 'react'
import '../css/Table.css'
import { Link } from 'react-router-dom'
import { useHttp } from '../hooks/http.hook'
import { useMessage } from '../hooks/message.hook'

function TableSystem({ _id, name, gateway, createdAt }) {

    const message = useMessage()
    const { loading, request, error, clearError } = useHttp()

    useEffect(() => {
        message(error)
        clearError()
    }, [error, message, clearError])


    const url = "http://localhost:5000/api/systems"

    const deleteHandler = async () => {
        try {
            const data = await request(url, 'DELETE', { _id })
            message(data.message)
        } catch (error) {
            console.error('Ошибка:', error);
        }
    }


    return (
        <tr key={_id} className="content__items">
            <td className="content__block">
                <Link to={`/system/${_id}`} >
                    <span></span>
                    <span className="table__tetx">{name}</span>
                </Link>
            </td>
            <td className="content__block">
                <span className="table__tetx">{gateway}</span>
            </td>
            <td className="content__block">
                <span className="table__tetx">{createdAt}</span>
            </td>
            <td className="content__block">
                <button
                    onClick={deleteHandler}
                    className="edit__btn"
                    disabled={loading}
                    >
                    <i class="fas fa-pen" />
                </button>
                <button className="del__btn"><i class="fa fa-trash" /></button>
            </td>
        </tr>
    )
}

export default TableSystem
