import React, { useState,useEffect } from 'react'
import '../css/Menu.css'
import { useHttp } from '../hooks/http.hook'
import { useMessage } from '../hooks/message.hook'

function Menu() {
    const [Score, SetScore] = useState(false);
    const [form, setForm] = useState({
        name: '', gateway: ''
      })
      
  const message = useMessage()
  const { loading, request, error, clearError} = useHttp()


    useEffect(() => {
        console.log('ERROR' + error)
        message(error) 
        clearError()
      }, [error, message, clearError])


    
    const url = "http://localhost:5000/api/systems"

    const OpenForm = () => {
        SetScore(true)
    };

    const CloseForm = () => {
        SetScore(false)
    };

     const PostInfoSystem = async () => {
        try {
            const data = await request(url,'POST', {...form} )
            message(data.message)
          } catch (error) {
            console.error('Ошибка:', error);
          }
      
    }

    const changeHandler = event => {
        setForm({ ...form, [event.target.name]: event.target.value })
      }


    return (
        <div className="menu_system">
            <button onClick={OpenForm} className="add__btn">Добавить</button>
            {
                Score ?
                    <div className="system__create">
                        <div className="system__info">
                            <span className="system__title">
                                Создание ИС
                            </span>
                            <button className="sys_cl"
                                onClick={CloseForm}>
                                <span className="system__close"></span>
                            </button>

                        </div>
                        <form className="Form__system">
                            <ul className="group__items">
                                <li>
                                    <input className="system__name"
                                        type="text"
                                        name="name"
                                        placeholder="Название ИС"
                                        onChange={changeHandler}
                                        value={form.name} />
                                </li>
                                <li>
                                    <input className="system__name"
                                        type="text"
                                        name="gateway"
                                        placeholder="Сеть"
                                        onChange={changeHandler}
                                        value={form.gateway} />
                                </li>
                                <li>
                                    <button className="system__btn"
                                        type="submit"
                                        disabled={loading}
                                        onClick={PostInfoSystem}
                                         > Добавить </button>
                                </li>
                            </ul>
                        </form>
                    </div>
                    : false
            }
        </div>
    )
}

export default Menu
