import React, { useState } from 'react'
import '../css/Menu.css'
import { useHttp } from '../hooks/http.hook';

function Menu() {
    const url = "http://localhost:5000/api/systems"
    const [Score, SetScore] = useState(false);
    const [SystemName, SetSystemName] = useState('');
    const [SystemNetwork, SetSystemNetwork] = useState('');

    const [form, setForm] = useState({
        email: '', password: ''
      })


      console.log("Form"+{...form})



    const {request} = useHttp()

    const OpenForm = () => {
        SetScore(true)
    };

    const CloseForm = () => {
        SetScore(false)
    };

     const PostInfoSystem = async () => {
        console.log("GO"+ {SystemName,SystemNetwork})
        try {
            const data = await request(url,'POST', {SystemName,SystemNetwork} )
            console.log("datassssss"+data)
          } catch (error) {
            console.error('Ошибка:', error);
          }
        SetScore(false);
    }

    const handleNameChange = (e) => {

        SetSystemName(e.value)
    }

    const handleNetworkChange = (e) => {
        SetSystemNetwork(e.value)
    }


    return (
        <div className="menu_system">
            <button onClick={OpenForm} className="add__btn">Добавить</button>
            {
                Score ?
                    <div className="system__create">
                        <div className="system__info">
                            <span className="system__title">
                                Создание ИС
                            </span>
                            <button className="sys_cl"
                                onClick={CloseForm}>
                                <span className="system__close"></span>
                            </button>

                        </div>
                        <form className="Form__system">
                            <ul className="group__items">
                                <li>
                                    <input className="system__name"
                                        type="text"
                                        name="system_name"
                                        placeholder="Название ИС"
                                        onChange={handleNameChange}
                                        value={SystemName} />
                                </li>
                                <li>
                                    <input className="system__name"
                                        type="text"
                                        name="system_gatewa"
                                        placeholder="Сеть"
                                        onChange={handleNetworkChange}
                                        value={SystemNetwork} />
                                </li>
                                <li>
                                    <button className="system__btn"
                                        type="submit"
                                        onClick={PostInfoSystem}
                                         > Добавить </button>
                                </li>
                            </ul>
                        </form>
                    </div>
                    : false
            }
        </div>
    )
}

export default Menu
