import {useCallback} from 'react'
import { useToasts,ToastProvider } from 'react-toast-notifications'

export const useMessage = () => {
  const { addToast } = useToasts()

  return useCallback(text => {
    if (text) {
      addToast(text, {
        appearance: 'error',
        autoDismiss: true,
      })
    }
  }, [])
}


const App = () => (
  <ToastProvider>
    <FormWithToasts />
  </ToastProvider>
)
