import {useCallback} from 'react'

export const useMessage = () => {

  const divStyle = {
    color: 'blue',
  };

  return useCallback(text => {
    if (window.M && text) {
      window.M.toast({ html: text, classes: divStyle})
    }
  }, [])
}
