import React,{useCallback} from 'react'

export const useMessage = () => {

  const divStyle = {
    color: 'blue',
    backgroundImage: 'url(' + imgUrl + ')',
  };

  return useCallback(text => {
    if (text) {
      return(
      <div style={divStyle}> {text} </div>
      )}
  }, [])
}
