import React, { useState, useEffect } from 'react'
import axios from 'axios';
import TableSheme from '../Components/TableSheme'
import Menu from '../Components/Menu'
import '../css/Table.css'
import { useParams } from 'react-router-dom'
import { useHttp } from '../hooks/http.hook'
import { useMessage } from '../hooks/message.hook'



function SystemInfo() {

    const [items, setItems] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);

    //потом перенсти

    const [selectedFile, setselectedFile] = useState(null)


    const message = useMessage()
    const { request, error, clearError } = useHttp()

    const { id } = useParams()
    const url = `http://localhost:5000/api/uploads/${id}`

    useEffect(() => {
        message(error)
        clearError()
    }, [error, message, clearError])


    useEffect(async () => {
        try {
            const data = await request(url, 'GET')
            setIsLoaded(true);
            setItems(data);
        } catch (error) {
            setIsLoaded(true);
        }
    }, [isLoaded])


    //перенести потом
    const onChangeHandler = event => {
        console.log(event.target.files[0])
        setselectedFile(event.target.files[0])
    }

    const onClickHandler = () => {
        const data = new FormData()
        data.append('file', selectedFile, selectedFile.name)
        axios.post(url, data);
    }



    const dowlandScheme = (_id) => {
        console.log(_id)
        
        axios({
            url: `http://127.0.0.1:5000/api/uploads/schema/${_id}`,
            method: 'GET',
            responseType: 'blob', // important
          }).then((response) => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'file.odt');
            document.body.appendChild(link);
            link.click();
          });
    }

    if (error) {
        return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Загрузка...</div>;
    } else {
        return (
            <div>

                <div className="system__btn">
                    <input type="file" name="file" onChange={onChangeHandler} />
                    <button type="button" class="btn btn-success btn-block" onClick={onClickHandler}>Загрузить</button>

                </div>

                < table className="table__system">
                    <thead>
                        <tr>
                            <th className="title__head">
                                <span className="table__tetx"> Наименование файла </span>
                            </th>
                            <th className="title__head">
                                <span className="table__tetx">Размер</span>
                            </th>
                            <th className="title__head">
                                <span className="table__tetx">Дата изменения</span>
                            </th>
                            <th className="title__head">
                                <span className="table__tetx">Виджеты</span>
                            </th>
                        </ tr>
                    </thead>
                    <tbody>
                        {
                            items.map((obj) => {
                                return (
                                    <TableSheme key={obj._id} {...obj} hanldeVidjet={dowlandScheme} />
                                )
                            })
                        }
                    </ tbody>
                </ table>
            </div>
        )
    }
}

export default SystemInfo
