import React from 'react'

function Table_Pagination({ postsPerPage, totalPosts, paginate }) {
    console.log(postsPerPage, totalPosts)
    const pageNumber = []
    for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
        pageNumber.push(i)
    }

    const checkPaginateNumber = (number) => {
        paginate(number)
    }   

    return (
        <nav className="nav__pagination">
            <span className="text__pagination">Showing 11 to 20 of 57 entries</span>
            <ul className="table__pagination">
                <li className="page__item">
                    <button className="page__link">
                        Previous
                    </button>
                </li>
                {
                    pageNumber.map(number =>
                        <li key={number} className="page__item">
                            <button onClick={ () => checkPaginateNumber(number)} className="page__link">
                                {number}
                            </button>
                        </li>
                    )
                }
                <li className="page__item">
                    <button className="page__link">
                        Next
                    </button>
                </li>
            </ul>
        </nav>
    )
}

export default Table_Pagination
