import React, { useState, useRef } from 'react'
import './Table.css'
import { Link } from 'react-router-dom'

function TableSystem({ _id, name, gateway, createdAt, hanldeVidjet }) {

    const [clickEdit, setclickEdit] = useState(false)
    const [editText, seteditText] = useState({
        name: name, gateway: gateway
    })

    const [clickMore, setclickMore] = useState(false)
    const mainRef = useRef(null)

    const editHandler = () => {
        setclickEdit(true)
    }

    const deleteHandler = () => {
        hanldeVidjet(_id, 'DELETE')
    }

    const changeHandler = event => {
        seteditText(event.target.value)
    }

    const changeFocus = () => {
        setclickEdit(false)
    }


    const handleKeyPress = (event) => {
        if (event.key == 'Enter') {

            setclickEdit(false)
        }
    }


    const onClickHandlerMore = async () => {
        if (clickMore === true) {
            await setclickMore(false)
        }
        else {
            await setclickMore(true)
            mainRef.current.focus()
        }
    }


    const changeFocusMore = () => {
        setclickMore(false)
    }

    return (
        <tr key={_id} className="content__items">
            {
                clickEdit === false ?
                    <td className="content__block">
                        <Link to={{
                            pathname: `/system/${_id}`,
                            name: name,
                            gateway: gateway,
                            createdAt: createdAt
                        }} >
                            <span></span>
                            <span className="table__tetx">{name}</span>
                        </Link>
                    </td>
                    :
                    <td className="content__block">
                        <input
                            className="table__tetx"
                            type="text"
                            name="name"
                            onBlur={changeFocus}
                            onChange={changeHandler}
                            onKeyPress={handleKeyPress}
                            value={editText.name} />
                    </td>
            }

            <td className="content__block">
                <span className="table__tetx">{gateway}</span>
            </td>
            <td className="content__block">
                <span className="table__tetx">{createdAt}</span>
            </td>
            <td className="content__block">
                <button
                    class="btn__more"
                    onClick={onClickHandlerMore}
                >
                    <span className="icon__more" />
                </button>
                {clickMore === true
                    ?
                    <div
                        ref={mainRef}
                        tabIndex="1"
                        onBlur={changeFocusMore}
                        className="window__more">
                        <ul className="list__vidj">
                            <li
                                className="vidj__post"
                                onClick={editHandler}
                            >Переименовать</li>
                            <li
                                className="vidj__post"
                                onClick={deleteHandler}
                            >Удалить</li>
                        </ul>
                    </div>
                    : false
                }
            </td>
        </tr>
    )
}

export default TableSystem
